mapper.update:

update mymember 
   set mem_name = #{memName}, 
        mem_tel = #{memTel},  
		mem_addr = #{memAddr} 
 where mem_id = #{memId}

mapper.select:

	select * from mymember 
	 where 1=1

       and mem_id = #{memId}


	   and mem_name = #{memName}


	   and mem_Tel = #{memTel}


	   and mem_addr like '%' || #{memAddr} || '%'
